import React from "react";

function DailyForecast({ data }) {
  return (
    <div className="DailyForecast">
      {data ? (
        data.daily ? ( // Check if data and daily property exist
          data.daily.slice(1, 6).map((day, index) => (
            <div key={index} className="forecast-item">
              {/* Display daily forecast data here */}
              <p>{new Date(day.dt * 1000).toLocaleDateString()}</p>
              <img
                src={`http://openweathermap.org/img/wn/${day.weather[0].icon}@2x.png`}
                alt="Weather icon"
              />
              <p>High: {day.temp.max}°C</p>
              <p>Low: {day.temp.min}°C</p>
            </div>
          ))
        ) : (
          <p>No daily forecast data available.</p>
        )
      ) : (
        <p>Loading daily forecast...</p>
      )}
    </div>
  );
}

export default DailyForecast;
