import React, { useState } from "react";
import {
  TextField,
  Button,
  Box,
  Typography,
  Card,
  CardContent,
  AppBar,
  Toolbar,
  Container,
  Grid,
  CardActions,
  Link,
  Footer,
} from "@mui/material";
import axios from "axios";
import CurrentWeather from "./components/CurrentWeather";

function App() {
  const [searchCity, setSearchCity] = useState("");
  const [weatherData, setWeatherData] = useState(null);
  const [errorMessage, setErrorMessage] = useState("");

  const handleCityChange = (e) => {
    setSearchCity(e.target.value);
    setErrorMessage("");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!searchCity) {
      setErrorMessage("Please enter a city name");
      return;
    }
    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${searchCity}&appid=7f88633674eabfb7bb8da25149fa9582&units=metric`
      );
      setWeatherData(response.data);
      setErrorMessage("");
    } catch (error) {
      console.error(error);
      setErrorMessage("Error fetching weather data");
    }
  };

  return (
    <Box sx={{ display: "flex", flexDirection: "column", minHeight: "100vh" }}>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Weather App
          </Typography>
          {/* Add search bar or other navigation elements here */}
        </Toolbar>
      </AppBar>
      <Container maxWidth="md">
        {/* Main content */}
        <Box sx={{ flexGrow: 1 }}>
          {/* Search form */}
          {/* Current weather card */}
          {/* Additional weather information (e.g., hourly, daily forecast) */}
        </Box>
      </Container>
      <Footer sx={{ mt: "auto", bgcolor: "#f5f5f5", p: "2rem" }}>
        <Typography variant="body2" color="textSecondary" align="center">
          {"Copyright © "}
          {new Date().getFullYear()}
          {" Your Company Name"}
        </Typography>
      </Footer>
    </Box>
  );
}

export default App;
