import React, { useState } from "react";
import { TextField, Button, Box } from "@mui/material";
import axios from "axios";
import CurrentWeather from "./components/CurrentWeather";
import Header from "./components/Header";
import Footer from "./components/Footer";

function App() {
  const [searchCity, setSearchCity] = useState("");
  const [weatherData, setWeatherData] = useState(null);
  const [errorMessage, setErrorMessage] = useState("");

  const handleCityChange = (e) => {
    setSearchCity(e.target.value);
    setErrorMessage("");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!searchCity) {
      setErrorMessage("Please enter a city name");
      return;
    }
    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${searchCity}&appid=7f88633674eabfb7bb8da25149fa9582&units=metric`
      );
      setWeatherData(response.data);
      setErrorMessage("");
    } catch (error) {
      console.error(error);
      setErrorMessage("Error fetching weather data");
    }
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        background: weatherData
          ? `linear-gradient(to bottom, ${getBackgroundColor(
              weatherData.weather[0].main
            )}, #e7e7e7)`
          : "#e7e7e7",
      }}
    >
      <Header />
      <Box
        sx={{
          flexGrow: 1,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <Box
          sx={{
            mb: 3,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <form onSubmit={handleSubmit}>
            <TextField
              label="City Name"
              variant="outlined"
              value={searchCity}
              onChange={handleCityChange}
              error={!!errorMessage}
              helperText={errorMessage}
              fullWidth
            />
            <Button type="submit" variant="contained" sx={{ mt: 2 }}>
              Search
            </Button>
          </form>
        </Box>
        {weatherData && <CurrentWeather data={weatherData} />}
      </Box>
      <Footer />
    </div>
  );
}

function getBackgroundColor(weatherType) {
  switch (weatherType) {
    case "Clear":
      return "#F0E68C"; // light yellow
    case "Clouds":
      return "#C0C0C0"; // gray
    case "Rain":
      return "#4169E1"; // dark blue
    case "Snow":
      return "#A9A9A9"; // light gray
    default:
      return "#ffffff"; // light gray
  }
}

export default App;
